mapper.select:
select wil.ivil_id, wil.ivih_id, wil.product_id, wil.lc_code, wil.iv_fifo_time, wil.iv_qty, wil.product_date, wil.iv_effective_date, wil.batch_no, cp.product_no, cp.product_name, cp.product_name_local, cp.product_barcode, cp.product_upc_code, cp.product_category_id, cp.spec_type, cp.brand, cp.place_of_origin, cp.unit_gross_weight, cp.product_length, cp.product_width, cp.product_height, cp.unit_vol, cp.stock_unit, cp.product_color, cp.product_size, cp.cost_reference, cp.currency_id, cp.selling_price_reference, cp.selling_currency_id, cp.texture, cp.memo, cp.status, cp.sale_status, cp.image_path, cp.mid_bag_qty, cp.mid_bag_weight, cp.mid_bag_length, cp.mid_bag_width, cp.mid_bag_height, cp.mid_bag_vol, cp.mid_bag_barcode, cp.big_bag_qty, cp.big_bag_weight, cp.big_bag_length, cp.big_bag_width, cp.big_bag_height, cp.big_bag_vol, cp.big_bag_barcode from wms_init_line wil LEFT JOIN cdm.cdm_product cp ON cp.product_id = wil.product_id
and wil.ivil_id ${id}
and wil.ivih_id ${ivihId}
and wil.product_id ${productId}
and wil.lc_code ${lcCode}
and wil.iv_fifo_time ${ivFifoTime}
and wil.iv_qty ${ivQty}
and wil.product_date ${productDate}
and wil.iv_effective_date ${ivEffectiveDate}
and wil.batch_no ${batchNo}
order by ${orderBy}
