mapper.select:
select

from wms_inventory
where  lc_code=#{lcCode}
and product_id=#{productId}
order by iabId

select

from wms_inventory wi
left join cdm.cdm_product cp
on wi.product_id = cp.product_id


        and wi.lc_code ${lcCode}


        and wi.product_no ${productNo}


        and cp.product_name ${productName}


        and cp.product_barcode ${productBarcode}


        and wi.lc_type ${lcType}



    order by ${orderBy}
