mapper.select:
select

from wms_inventory
where
company_id = #{companyId}
and warehouse_id=#{warehouseId}
and lc_code=#{lcCode}
and product_id=#{productId}
order by iab_Id

select

from wms_inventory wi
left join cdm.cdm_product cp on wi.product_id = cp.product_id


        and wi.lc_code ${lcCode}



        and cp.product_no ${productNo}



        and cp.product_name ${productName}



        and cp.product_barcode ${productBarcode}



        and wi.lc_type ${lcType}



         and wi.warehouse_id ${warehouseId}



        and wi.company_id ${companyId}
        and wi.company_id = cp.company_id




    order by ${orderBy}

SELECT IFNULL(SUM(iv_qty), 0)
FROM wms_inventory
WHERE product_id=#{productId}
AND warehouse_id=#{warehouseId}
AND company_id=#{companyId}

SELECT a.lc_code,sum(iv_qty) iv_qty FROM wms_inventory a
WHERE a.company_id = #{companyId} AND a.warehouse_id = #{warehouseId}
AND a.product_id = #{productId} AND a.lc_type = 'Z'
GROUP BY a.lc_code
ORDER BY ABS(CONVERT(replace(replace(a.lc_code,'Z',''),'-',''), SIGNED)-#{lcCodeNumber})
LIMIT 1;

SELECT ps.id, iv.product_id, iab.product_no, iab.product_name, iab.product_barcode, iab.product_upc_code, iab.big_bag_rate/iab.mid_bag_rate big_bag_specification, iab.mid_bag_rate, iab.big_bag_rate, iab.big_bag_vol, iab.big_bag_weight, iv.lc_code, lc.lc_vol/1000000 location_volume, lc.lc_vol/1000000*0.85 storage_volume, ROUND(lc.lc_vol/1000000*0.85/iab.big_bag_vol) number_large_package, SUM(iv.iv_qty) iv_qty, ROUND(SUM(iv.iv_qty)/iab.big_bag_rate) big_count, ROUND(MOD(SUM(iv.iv_qty),iab.big_bag_rate) / iab.mid_bag_rate) middle_count, MOD(SUM(iv.iv_qty),iab.mid_bag_rate) small_count, IFNULL(ps.ceiling_multiple,2) ceiling_multiple, IFNULL(ps.limit_multiple,0.2) limit_multiple, ROUND(IFNULL(ps.ceiling_multiple,2)* ROUND(lc.lc_vol/1000000*0.85/iab.big_bag_vol)) inventory_ceiling_big_count, ROUND(IFNULL(ps.limit_multiple,0.2)* ROUND(lc.lc_vol/1000000*0.85/iab.big_bag_vol)) inventory_limit_big_count, CASE WHEN ROUND(SUM(iv.iv_qty)/iab.big_bag_rate) > ROUND(IFNULL(ps.ceiling_multiple,2)* ROUND(lc.lc_vol/1000000*0.85/iab.big_bag_vol)) THEN 'RISE' WHEN ROUND(SUM(iv.iv_qty)/iab.big_bag_rate) < ROUND(IFNULL(ps.limit_multiple,0.2)* ROUND(lc.lc_vol/1000000*0.85/iab.big_bag_vol)) THEN 'DROP' ELSE '-' END lc_type FROM wms_inventory iv LEFT JOIN wms_iv_attributebatch iab ON iv.iab_id = iab.iab_id LEFT JOIN wms_location lc ON iv.lc_code = lc.lc_code LEFT JOIN wms_product_sample ps ON iv.warehouse_id=ps.warehouse_id AND iv.product_id=ps.product_id
iv.lc_type = 'S' AND iv.lc_code NOT IN ( SELECT distinct wlw.sample_lc_code FROM wms_lift_work wlw )
and iv.lc_code ${lcCode}
and iab.product_no ${productNo}
and iab.product_name ${productName}
and iab.product_barcode ${productBarcode}
and iv.warehouse_id ${warehouseId}
and iv.company_id ${companyId}
order by ${orderBy}
GROUP BY iab.product_id,iv.lc_code
