mapper.select:
select

from wms_location where lc_code in

    #{item}

select

from wms_location


        and warehouse_id = #{warehouseId}


        and lc_code = #{lcCode}

select
    wl.location_id,
    wl.lc_code,
    wl.warehouse_id,
    wl.warehouse_code,
    wl.lc_aisle,
    wl.lc_x,
    wl.lc_y,
    wl.lc_z,
    wl.lc_type,
    wl.lc_length,
    wl.lc_width,
    wl.lc_height,
    wl.lc_vol,
    wl.lc_sustainweight,
    wl.lc_putawaylock,
    wl.lc_pickinglock,
    wl.memo,
    cw.warehouse_name
from wms_location wl
left join cdm.cdm_warehouse cw on wl.warehouse_id = cw.warehouse_id and wl.warehouse_code = cw.warehouse_code


        and wl.location_id ${locationId}


        and wl.lc_code ${lcCode}


        and wl.warehouse_id ${warehouseId}


        and wl.warehouse_code ${warehouseCode}


        and wl.lc_aisle ${lcAisle}


        and wl.lc_x ${lcX}


        and wl.lc_y ${lcY}


        and wl.lc_z ${lcZ}


        and wl.lc_type ${lcType}


        and wl.lc_length ${lcLength}


        and wl.lc_width ${lcWidth}


        and wl.lc_height ${lcHeight}


        and wl.lc_vol ${lcVol}


        and wl.lc_sustainweight ${lcSustainweight}


        and wl.lc_putawaylock ${lcPutawaylock}


        and wl.lc_pickinglock ${lcPickinglock}


        and wl.memo ${memo}


        and (wl.lc_putawaylock = 0 and wl.lc_pickinglock = 0)


        and (wl.lc_putawaylock = 1 or wl.lc_pickinglock = 1)


        and wl.lc_putawaylock = 1


        and wl.lc_pickinglock = 1



    order by ${orderBy}

select
    distinct wl.lc_aisle,
    wl.warehouse_id,
    wl.warehouse_code,
    wl.location_id
from wms_location wl


        and wl.warehouse_id ${warehouseId}


        and wl.warehouse_code ${warehouseCode}


GROUP BY wl.lc_aisle

    order by ${orderBy}

select wl.location_id, wl.lc_code, wl.warehouse_id, wl.warehouse_code, wl.lc_aisle, wl.lc_x, wl.lc_y, wl.lc_z, wl.lc_type, wl.lc_length, wl.lc_width, wl.lc_height, wl.lc_vol, wl.lc_sustainweight, wl.lc_putawaylock, wl.lc_pickinglock, wl.memo, COUNT(wi.product_id) product_count, SUM(wi.iv_qty) AS qty from wms_location wl LEFT join wms_inventory wi ON wi.lc_code = wl.lc_code
and wi.warehouse_id ${warehouseId}
and wi.company_id ${companyId}
and wl.warehouse_id ${warehouseId}
and wl.warehouse_code ${warehouseCode}
and wl.lc_aisle ${lcAisle}
GROUP BY wl.lc_code,wl.warehouse_id
order by ${orderBy}

select wl.location_id, wl.lc_code, wl.warehouse_id, wl.warehouse_code, wl.lc_aisle, wl.lc_x, wl.lc_y, wl.lc_z, wl.lc_type, wl.lc_length, wl.lc_width, wl.lc_height, wl.lc_vol, wl.lc_sustainweight, wl.lc_putawaylock, wl.lc_pickinglock, wl.memo, COUNT(wi.product_id) + COUNT(wlr.product_id) product_count, SUM(wi.iv_qty) AS qty from wms_location wl LEFT join wms_inventory wi ON wi.lc_code = wl.lc_code
and wi.warehouse_id ${warehouseId}
and wi.company_id ${companyId}
LEFT JOIN wms_lc_recommend wlr ON wl.lc_code = wlr.lc_code
and wlr.warehouse_id ${warehouseId}
and wlr.company_id ${companyId}
and wl.warehouse_id ${warehouseId}
and wl.warehouse_code ${warehouseCode}
and wl.lc_aisle ${lcAisle}
GROUP BY wl.lc_code,wl.warehouse_id
order by ${orderBy}
