mapper.select:
SELECT

FROM wms_roo_header wrh
INNER JOIN cdm.cdm_warehouse cw ON cw.warehouse_id = wrh.warehouse_id


        and wrh.roo_id ${id}


        and wrh.roo_code ${rooCode}


        and wrh.order_type ${orderType}


        and wrh.ro_id ${roId}


        and wrh.ro_code ${roCode}


        and wrh.cust_order_no ${custOrderNo}


        and wrh.company_id ${companyId}


        and wrh.warehouse_id ${warehouseId}


        and wrh.warehouse_code ${warehouseCode}


        and wrh.supplier_id ${supplierId}


        and wrh.customer_id ${customerId}


        and wrh.total_qty ${totalQty}


        and wrh.tmp_putaway_qty ${tmpPutawayQty}


        and wrh.putaway_qty ${putawayQty}


        and wrh.ro_status ${roStatus}


        and wrh.memo ${memo}


        and wrh.create_user ${createUser}


        and wrh.create_time ${createTime}



    order by ${orderBy}

select DATE_FORMAT(date_add(p.putaway_time,interval -${hour} hour),'%Y-%m-%d') as date_time, ro.supp_cust_name, po.contract_no, IFNULL(sum(pl.paol_qty / iab.big_bag_rate), 0) as sum_big_count, IFNULL(sum(pl.paol_qty), 0) as sum_count, IFNULL(sum(rol.unit_price * pl.paol_qty), 0) as sum_amount, iab.product_name, cu.currency_name, cu.currency_ename from wms.wms_pao_header p left join wms.wms_pao_line pl on pl.pao_id = p.pao_id left join wms.wms_iv_attributebatch iab on pl.iab_id = iab.iab_id left join wms.wms_ro_line rol on rol.rol_id = pl.rol_id left join wms.wms_ro_header ro on ro.ro_id = p.ro_id left join oms.oms_purchase_order po on po.po_id = ro.po_id left join cdm.cdm_currency cu on cu.currency_id = rol.unit_price_currency_id
and p.company_id ${companyId}
and p.putaway_time ${putawayTime}
group by DATE_FORMAT(date_add(p.putaway_time,interval -${hour} hour),'%Y-%m-%d'),ro.supp_cust_name,iab.product_name ORDER BY date_time DESC
